INSERT INTO user_profiles (id, user_id, first_name, last_name, phone, about, short_about, location, created_at, updated_at) VALUES ('cmfbgo83u0001zjo4o37kw8hv', 'turso_1', 'VoiceoverGuy - Yorkshire Voice Recording Studio', NULL, '07973350178', 'A broadcast quality studio for hire in West Yorkshire in the UK. \r\n\r\nSuitable for Audiobook voiceover recording, Interviews including down the line interviews connecting with broadcasters worldwide, Podcast interviews, commercial or training video voice recordings and any form of recording spoken word in a beautiful comfortable studio in West Yorkshire.\r\n\r\nA favourite of Emmerdale television stars and Kammy Chris Kamara\r\n \r\nKit includes Neumann U87, TLM 103 and Sennheiser mk416 Mics. Apollo X4 &amp; X6. Neve pre amps\r\nPerfectly treated acoustic voiceover booth for standing or seated voiceover recordings.\r\n\r\nAs a British Male Voiceover, my own voiceover clients include Apple, Tesco, RBS, Vodafone, SAGE, BBC TV, ITV, Radio 2, LEGO, Centre Parcs, Butlins, Asda, Hotels.com among others.\r\n\r\nThe studio can be booked per hour and includes an engineer. The rates listed are subject to VAT. \r\n\r\nFree parking on site.    ', 'Broadcast quality Studio in West Yorkshire. Audiobook, Podcasts, Commercials, interviews, training film recording and more.', 'United Kingdom', '2025-09-08T18:34:37.193Z', '2025-09-08T18:34:37.193Z');
INSERT INTO user_profiles (id, user_id, first_name, last_name, phone, about, short_about, location, created_at, updated_at) VALUES ('cmfbgo8p80037zjo4nuri2ja9', 'turso_1848', 'VoiceoverGuy - Yorkshire Voice Recording Studio', NULL, '07973350178', 'A broadcast quality studio for hire in West Yorkshire in the UK. Suitable for Audiobook voiceover recording, Interviews including down the line interviews connecting with broadcasters worldwide, Podcast interviews, commercial or training video voice recordings and any form of recording spoken word in a beautiful comfortable studio in West Yorkshire.\r\n\r\nA favourite of Emmerdale television stars and Kammy Chris Kamara\r\n \r\nKit includes Neumann U87, TLM 103 and Sennheiser mk416 Mics. Apollo X4 &amp; X6. Neve pre amps\r\nPerfectly treated acoustic voiceover booth for standing or seated voiceover recordings.\r\n\r\nAs a British Male Voiceover, my own voiceover clients include Apple, Tesco, RBS, Vodafone, SAGE, BBC TV, ITV, Radio 2, LEGO, Centre Parcs, Butlins, Asda, Hotels.com among others.\r\n\r\nThe studio can be booked per hour and includes an engineer. The rates listed are subject to VAT. \r\n\r\nFree parking on site.     ', 'Broadcast quality Studio in West Yorkshire. Audiobook, Podcasts, Commercials, interviews, training film recording and more.', 'United Kingdom', '2025-09-08T18:34:37.964Z', '2025-09-08T18:34:37.964Z');
INSERT INTO user_profiles (id, user_id, first_name, last_name, phone, about, short_about, location, created_at, updated_at) VALUES ('cmfbgo9a1006jzjo4r2yktch5', 'turso_1851', 'S2Blue', NULL, '0161 905 2662', 'Over the years we have cultivated an impressive client base, which includes all of the major broadcasters in British media. Our productions have been heard on radio and TV stations across the world.\r\n\r\nIn the UK our work has aired on BBC, Global Radio, Bauer, SKY, ITV, MTV, Discovery Network as well as many other ILR and local radio stations. \r\n\r\nOur professional broadcast-quality studio is equipped with the latest ProTools Software. The S2Blue Production team are experienced in a wide range of audio projects, including broadcast commercials and Corporate projects in multiple languages.&nbsp;\r\n\r\nWe can record remotely using Cleanfeed, Source Connect, or ISDN, and our sessions are easily accessible for clients to attend either in person or via Skype, Zoom, or Phone Patch.&nbsp;\r\n\r\nWe can offer discounted rates for larger projects and will always do our best to accommodate last minute bookings where we can - just give us a call!', 'S2Blue is the innovative audio company that produce voiceovers, music, jingles, radio and TV commercials.', 'United Kingdom', '2025-09-08T18:34:38.713Z', '2025-09-08T18:34:38.713Z');
INSERT INTO user_profiles (id, user_id, first_name, last_name, phone, about, short_about, location, created_at, updated_at) VALUES ('cmfbgo9mp008lzjo4uyl2osgz', 'turso_1852', 'Get Carter', NULL, NULL, 'Paul is the founder and Managing Director of Get Carter Productions. He&rsquo;s worked for radio groups across the UK, rising through the ranks to the level of Radio Group Creative Director, before launching his own company in 2001. He&rsquo;s won many awards, including the highly coveted New York Radio Festivals and London Internationals. Paul creates campaigns for clients including: Jet 2, Bristol Street Motors and Safestyle UK.', 'Get Carter Productions. Radio - TV/Video - Telecoms - Voiceovers - Music Licensing', 'United Kingdom', '2025-09-08T18:34:39.169Z', '2025-09-08T18:34:39.169Z');
INSERT INTO user_profiles (id, user_id, first_name, last_name, phone, about, short_about, location, created_at, updated_at) VALUES ('cmfbgo9v2009xzjo47lhlju1n', 'turso_1854', 'Audio Take', NULL, '+44(0)1202 253599', 'Post Production, Music composition, Sound Design, Voice-overs, \r\nTV Commercials, Radio promos, Cinema Ads, Online videos, Games &amp; apps.', 'Audio post production recording studio based in Bournemouth UK', 'United Kingdom', '2025-09-08T18:34:39.470Z', '2025-09-08T18:34:39.470Z');
INSERT INTO user_profiles (id, user_id, first_name, last_name, phone, about, short_about, location, created_at, updated_at) VALUES ('cmfbgoa9j00cbzjo4qbdlo906', 'turso_1856', 'BKP Media Group', NULL, '+971 (0) 4 448 3600', 'World class production company specializing in music, audio &amp; video post production and video production\r\nBKP Media Group is the leading audio post production and music production company in the middle east.\r\nEstablished in Dubai media city for 10 years, BKP Media Group has provided the area with award winning\r\nmedia production solutions.', 'BKP Media Group is the leading audio post production and music production company in the middle east.', 'United Arab Emirates', '2025-09-08T18:34:39.991Z', '2025-09-08T18:34:39.991Z');
INSERT INTO user_profiles (id, user_id, first_name, last_name, phone, about, short_about, location, created_at, updated_at) VALUES ('cmfbgoana00ejzjo4cyfcb18x', 'turso_1863', 'A1 Vox', NULL, '020 7434 4404', 'Established in 1998. A1 VOX quickly built a reputation as Soho&rsquo;s friendliest sound studio. \r\nWe&rsquo;re bright, flexible, informal, efficient. Always ready to help and always up for adventure - we love an audio challenge! A1 VOX, the Spoken Word audio specialists, for recordings and remote links to anywhere by ISDN, Source Connect, Audio-over-IP. \r\nVoice-over artists available too - why not fix your studio and voice requirements in just one call?', 'London&rsquo;s friendliest sound studio', 'United Kingdom', '2025-09-08T18:34:40.486Z', '2025-09-08T18:34:40.486Z');
INSERT INTO user_profiles (id, user_id, first_name, last_name, phone, about, short_about, location, created_at, updated_at) VALUES ('cmfbgob0f00gpzjo4lbgnv6s2', 'turso_1864', 'Radio Jingles', NULL, '01803 322699', 'We have fully equipped studios within our Torquay, Devon premises that have all the up-to-date computerised audio recording software for all types of Radio Advertising. \r\n\r\nRadio Jingles has scriptwriters that are engaged in producing the voices and scripts that project your company&rsquo;s audio-image, that is memorable and distinct within the commercial advertising production.\r\nJingle production is something we excel in. Radiojingles can make your Company and it&rsquo;s Product stand out from the crowd.', 'Your Company and it&rsquo;s product is what we make interesting to the public', 'United Kingdom', '2025-09-08T18:34:40.959Z', '2025-09-08T18:34:40.959Z');
INSERT INTO user_profiles (id, user_id, first_name, last_name, phone, about, short_about, location, created_at, updated_at) VALUES ('cmfbgobdd00irzjo4bdsuqne5', 'turso_1865', 'Canongate Studios Edinburgh', NULL, '(0)131 555 4455', 'London designed ISDN Skype ADR &amp; Source Connect Audio Post Production recording studio in Edinburgh Scotland.\r\n We regularly record for BBC TV, ITV and Radio. Vast Voice Bank. Protools 11', 'Audio Post Production ADR ISDN Source Connect ipDTL Vo&rsquo;s', 'United Kingdom', '2025-09-08T18:34:41.425Z', '2025-09-08T18:34:41.425Z');
INSERT INTO user_profiles (id, user_id, first_name, last_name, phone, about, short_about, location, created_at, updated_at) VALUES ('cmfbgobpu00ktzjo41eb1dx3e', 'turso_1866', 'Voiceover Kickstart', NULL, '07973 445328', 'DEMO PRODUCTION\r\n\r\nProducer Guy Michaels has worked with nearly 800 clients since 2006.  This truly bespoke service, based in North London is unique, personal and to broadcast standards\r\n\r\nHISTORY + FACILITY\r\n\r\n2016 marks the 10th Anniversary of Round Island Voicereels - Round Island becomes Voiceover Kickstart during 2016.   Guy limits to working with just 2 clients a week giving full focus and dedication, resulting in a showreel that stands outs and represents the best of you.  \r\n\r\nTRAINING &amp; COACHING\r\n\r\nHaving taught voice+acting at RADA, GSA, Actors Guild, Actors Centre and more, Guy regularly works one-to-one in microphone and performance techniques. \r\n\r\nJoin the FREE 6 week programme at http://www.roundisland.net/vok-signup', 'Supercharging Voicer Careers Since 2006', 'United Kingdom', '2025-09-08T18:34:41.874Z', '2025-09-08T18:34:41.874Z');
INSERT INTO user_profiles (id, user_id, first_name, last_name, phone, about, short_about, location, created_at, updated_at) VALUES ('cmfbgoc3i00n1zjo49l4u2ghs', 'turso_1868', 'STL Audio', NULL, '(04) 801 5602', 'Our services include Music Recording, Mixing &amp; Mastering, Voice-overs, \r\nall Post Production and even rehearsal spaces.  \r\n\r\nOur recording studio is a stones throw from Cuba Street&rsquo;s cafes &ndash; just step outside and \r\nyou will be immersed in the creative culture that Wellington is world famous for.', 'Located in central Wellington, STL Audio specializes in providing one of the most \r\ncreative, professional and affordable recording studios', 'New Zealand', '2025-09-08T18:34:42.366Z', '2025-09-08T18:34:42.366Z');
INSERT INTO user_profiles (id, user_id, first_name, last_name, phone, about, short_about, location, created_at, updated_at) VALUES ('cmfbgocha00ovzjo4gr8cc9gt', 'turso_1869', 'Mike Cooper', NULL, '+1 (828) 333-5020', 'A great home studio with a view of Mt Pisgah, nestled in Hominy Valley, 25 minutes from downtown Asheville in Pisgah National Forest.\r\n\r\nI have a broadcast quality phone patch and can connect with all the VOIP codecs (BYO login) or with ISDN (via bridge).\r\n\r\nMy own voiceover work includes broadcast narrations for HISTORY, National Geographic and others, as well as corporate narrations for some of the biggest brands in the world.\r\n\r\nContact me for studio hire &ndash; or to book me for your own project!', 'British Voice Over Artist in America &ndash;\r\nbased just outside Asheville, NC', 'United States', '2025-09-08T18:34:42.862Z', '2025-09-08T18:34:42.862Z');
INSERT INTO user_profiles (id, user_id, first_name, last_name, phone, about, short_about, location, created_at, updated_at) VALUES ('cmfbgod0a00s1zjo4oyhvsvv9', 'turso_1870', 'Little Monster Media', NULL, NULL, 'Independant production house specialising in radio imaging, sound design FX and audio branding. Other services include DJ set intros, voiceover showreels, and audio editing.', 'Independant production house specialising in radio imaging, sound design FX and audio branding.', 'United Kingdom', '2025-09-08T18:34:43.546Z', '2025-09-08T18:34:43.546Z');
INSERT INTO user_profiles (id, user_id, first_name, last_name, phone, about, short_about, location, created_at, updated_at) VALUES ('cmfbgodaz00thzjo43wug66yf', 'turso_1872', 'Stevie Cripps', NULL, '07540620006', 'A young, yet exceptionally experienced voice, Stevie&rsquo;s tones can be heard on a multitude of platforms for leading brands. From advertising skin care products, engaging a stadium of 30,000, or doing promo &amp; imaging for TV &amp; radio, he&rsquo;s a naturally strong, cool, credible sounding guy, with a little edge &ndash; and very creative, too! BBC TV &amp; Radio 1, Nivea, Virgin Media, Coca Cola, HP, Nickelodeon, Speedo, Red Bull, Paramount, Selfridges. Can connect via ISDN, Skype  &amp;  Source Connect NOW!', 'A young, yet exceptionally experienced voice, Stevie is a naturally strong, cool, confident sounding guy, with a little edge.', 'United Kingdom', '2025-09-08T18:34:43.931Z', '2025-09-08T18:34:43.931Z');
INSERT INTO user_profiles (id, user_id, first_name, last_name, phone, about, short_about, location, created_at, updated_at) VALUES ('cmfbgodrs00w9zjo4m3gwly1f', 'turso_1873', 'Sixty Six Sound', NULL, '01202237266', 'Sixty Six Sound are specialists in radio imaging and production, including jingles and idents as well as live show openers. We take a fresh look at every project and using the latest plug-ins, sound design tools and techniques along with a vast array of voices to ensure that every piece we produce is unique. This is our insistence &ndash; no catalogues or libraries of old work rehashed for new shows. We only make new imaging, every time.', 'Sixty Six Sound are specialists in radio imaging and production, including jingles and idents as well as live show openers.', 'United Kingdom', '2025-09-08T18:34:44.536Z', '2025-09-08T18:34:44.536Z');
