INSERT INTO user_profiles (id, user_id, first_name, last_name, phone, about, short_about, location, created_at, updated_at) VALUES ('cmfbgorr6032nzjo4emssk2qb', 'turso_1917', 'Marc Graue Voice Over Studios', NULL, NULL, 'We are proud to have received 2 Grammys, a Clio, International Radio Festival Gold Awards, numerous Crystal Communicator Awards, Silver Mikes and a Telly Award.\r\n\r\nOur service is of unparalleled quality. We provide a flawless product while adhering to schedules and budgets. We offer four studios with Pro-Tools HD, ISDN, Source-Connect, phone patch, Skype and lock to picture capabilities with the quietest booths in Los Angeles. We will strive to accommodate your needs, however large or small at Marc Graue Voice Over Recording Studios.', 'Marc Graue Voice Over Recording Studios. The oldest voice over studio in Burbank, California. Known worldwide as the best.', 'United States', '2025-09-08T18:35:02.659Z', '2025-09-08T18:35:02.659Z');
