INSERT INTO user_profiles (id, user_id, first_name, last_name, phone, about, short_about, location, created_at, updated_at) VALUES ('cmfbgoup003izzjo4wegda43e', 'turso_1933', 'Diane Merritt Voice Overs', NULL, '843-446-5563', 'A Broadcast Quality Voice Over Studio equipped with Source Connect, Neumann TLM 103, Sennheiser MKH 416, dbx 286A processor, Adobe Audition CS6, acoustically treated room, an inground salt water pool and a cool beverage served in a coconut (the umbrella is extra)\r\n\r\nThis Mom Means Business and when I&#039;m not floating, I&#039;m voicing world wide TV &amp; Radio commercials &amp; Promos, Explainer Videos, e-Learning and phone greetings for clients such as Mercedes, Colgate, Lindt Chocolate, Warner Brothers, American Express, Coca Cola, Siemens Medical, Exxon/Mobile and more. \r\nWith a conversational, Professional, Mom sound, I am easy to work with, offer competitive rates w/ fast turnaround times. \r\n I also offer editing &amp; coaching.   I&#039;d love to work with you!', 'Your Studio Away From Studio is just 10 minutes from Downtown Greenville, SC', 'United States', '2025-09-08T18:35:06.468Z', '2025-09-08T18:35:06.468Z');
INSERT INTO user_profiles (id, user_id, first_name, last_name, phone, about, short_about, location, created_at, updated_at) VALUES ('cmfbgov6w03ltzjo4yfldaujg', 'turso_1934', 'Voiceover Oliver', NULL, '07809 475 351', 'The Voiceoveroliver studio is broadcast quality with Neumann TLM 67 Microphone positioned in a walk in booth. The booth has a 21 inch monitor with wireless keyboard and mouse.\r\n\r\nThe microphone is connected to a compression unit in a rack; a TL Audio IVORY 5060 Preset Valve Compressor. This can be bypassed or set at the level that you or your client requires.\r\n\r\nThe studio has a Prima LT ISDN machine and all studio kit is connected to a Soundcraft EPM 8 Mixer.\r\n\r\nPost MP3/WAV recording, you can listen back to your audio in Adobe Audition CS6 (Windows 10) on KRK Rokit 6 monitor speakers (pictured).\r\n\r\nwww.voiceoveroliver.co.uk', 'Voiceover - Oliver Dukcevic -  Providing a Professional ISDN Studio with Neumann TLM 67 microphone - Based just outside London Town.', 'United Kingdom', '2025-09-08T18:35:07.112Z', '2025-09-08T18:35:07.112Z');
